import React, { Fragment } from "react";
import Button from "./Button";
import { throwStatement } from "@babel/types";


export default class Themes extends React.Component {
  state = {
    theme: 'light'
  };

  changeToLightTheme = () => {
    this.setState({
      theme: 'light'
    });
  }

  changeToDarkTheme = () => {
    this.setState({
      theme: 'dark'
    });
  }

  render() {
    const { onButtonClick } = this.props;

    return (
      <div>
        <Button label="Light mode" value={this.state.theme} wide onButtonClick={this.changeToLightTheme}/>
        <Button label="Dark mode" value={this.state.theme} wide onButtonClick={this.changeToDarkTheme}/>
      </div>
    );
  }
}
